import React, { useEffect, useState } from 'react';
import { Project } from '../types';
import Footer from '../components/Footer';
import Header from '../components/Header';
import { Link } from 'react-router-dom';

const Projects: React.FC = () => {
    const [projects, setProjects] = useState<Project[]>([]);
    const [title, setTitle] = useState('');
    const [description, setDescription] = useState('');
    const [link, setLink] = useState('');
    const [image, setImage] = useState('');

    // Commented out API fetch for demo upload
    // useEffect(() => {
    //     const fetchProjects = async () => {
    //         const response = await fetch('http://localhost:5000/api/projects');
    //         const data = await response.json();
    //         setProjects(data);
    //     };
    //     fetchProjects();
    // }, []);

    const handleUpload = (project: Project) => {
        setProjects(prev => [project, ...prev]);
    };

    const handleSubmit = async (e: React.FormEvent) => {
        e.preventDefault();
        if (!title || !description || !link) return;
        await fetch('http://localhost:5000/api/projects', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ title, description, link, image })
        });
        handleUpload({ id: Date.now(), title, description, link, image });
        setTitle('');
        setDescription('');
        setLink('');
        setImage('');
    };

    return (
        <div className="main-content" style={{ minHeight: 'calc(100vh - 120px)', maxWidth: 1100, margin: '0 auto', padding: 24 }}>
            <Header
                title="Developer Portfolio"
                links={[
                    { name: 'Home', href: '/' },
                    { name: 'Projects', href: '/projects' },
                    { name: 'Contact', href: '/contact' }
                ]}
            />
            <div className="projects">
                <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: 32 }}>
                    <h1>My Projects</h1>
                    <Link to="/upload" style={{ padding: '10px 24px', background: '#22223b', color: '#fff', borderRadius: 4, textDecoration: 'none', fontWeight: 600 }}>Upload Project</Link>
                </div>
                <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(320px, 1fr))', gap: 32 }}>
                    {projects.map((project) => (
                        <div key={project.id} style={{ background: '#fff', borderRadius: 12, boxShadow: '0 2px 8px rgba(34,34,59,0.07)', padding: 20, display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
                            {project.image && <img src={project.image} alt={project.title} style={{ width: 180, height: 120, objectFit: 'cover', borderRadius: 8, marginBottom: 12 }} />}
                            <h2 style={{ margin: '8px 0' }}>{project.title}</h2>
                            <p style={{ color: '#444', marginBottom: 12 }}>{project.description}</p>
                            <a href={project.link} target="_blank" rel="noopener noreferrer" style={{ color: '#4a4e69', textDecoration: 'underline', fontWeight: 500 }}>View Project</a>
                        </div>
                    ))}
                </div>
                <Footer />
            </div>
        </div>
    );
};

export default Projects;
