import React, { useState } from 'react';
import Header from '../components/Header';
import Footer from '../components/Footer';

const Contact: React.FC = () => {
    const [amount, setAmount] = useState('');
    const [purpose, setPurpose] = useState('');
    const [period, setPeriod] = useState('');
    const [special, setSpecial] = useState('');
    const [submitted, setSubmitted] = useState(false);

    const handleSubmit = (e: React.FormEvent) => {
        e.preventDefault();
        setSubmitted(true);
    };

    // Contact form state
    const [msgName, setMsgName] = useState('');
    const [msgEmail, setMsgEmail] = useState('');
    const [msgMessage, setMsgMessage] = useState('');
    const [msgSent, setMsgSent] = useState(false);

    // Simulate sending email (replace with backend/email service in production)
    const handleMsgSubmit = (e: React.FormEvent) => {
        e.preventDefault();
        setMsgSent(true);
    };

    return (
        <main className="main-content" style={{ minHeight: 'calc(100vh - 120px)', maxWidth: 900, margin: '0 auto', padding: 24 }}>
            <Header
                title="Bsonat Portfolio"
                links={[
                    { name: 'Home', href: '/' },
                    { name: 'Projects', href: '/projects' },
                    { name: 'Contact', href: '/contact' }
                ]}
            />

         

            {/* Customer Message Form */}
            <section style={{ marginBottom: 48, display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'center' }}>
                <h1>Contact Me</h1>
                <div style={{ width: '100%', display: 'flex', justifyContent: 'center' }}>
                  {msgSent ? (
                      <div style={{ color: 'green', marginTop: 24 }}>
                          Thank you for your message! I will get back to you soon.
                      </div>
                  ) : (
                      <form onSubmit={handleMsgSubmit} style={{ display: 'flex', flexDirection: 'column', gap: 16, maxWidth: 500, width: '100%' }}>
                          <label>
                              Your Name
                              <input
                                  type="text"
                                  value={msgName}
                                  onChange={e => setMsgName(e.target.value)}
                                  required
                                  style={{ width: '100%', padding: 8, marginTop: 4 }}
                              />
                          </label>
                          <label>
                              Your Email
                              <input
                                  type="email"
                                  value={msgEmail}
                                  onChange={e => setMsgEmail(e.target.value)}
                                  required
                                  style={{ width: '100%', padding: 8, marginTop: 4 }}
                              />
                          </label>
                          <label>
                              Message
                              <textarea
                                  value={msgMessage}
                                  onChange={e => setMsgMessage(e.target.value)}
                                  rows={4}
                                  required
                                  placeholder="Type your message here..."
                                  style={{ width: '100%', padding: 8, marginTop: 4 }}
                              />
                          </label>
                          <button type="submit" style={{ padding: '10px 0', background: '#22223b', color: '#fff', border: 'none', borderRadius: 4, fontWeight: 600 }}>
                              Send Message
                          </button>
                      </form>
                  )}
                </div>
            </section>

            <Footer />
        </main>
    );
};

export default Contact;
