import React, { useState } from 'react';
import Header from '../components/Header';
import Footer from '../components/Footer';

const Contact: React.FC = () => {
    const [amount, setAmount] = useState('');
    const [purpose, setPurpose] = useState('');
    const [period, setPeriod] = useState('');
    const [special, setSpecial] = useState('');
    const [submitted, setSubmitted] = useState(false);

    const handleSubmit = (e: React.FormEvent) => {
        e.preventDefault();
        setSubmitted(true);
    };

    return (
        <main className="main-content" style={{ minHeight: 'calc(100vh - 120px)', maxWidth: 900, margin: '0 auto', padding: 24 }}>
            <Header
                title="Bsonat Portfolio"
                links={[
                    { name: 'Home', href: '/' },
                    { name: 'Projects', href: '/projects' },
                    { name: 'Contact', href: '/contact' }
                ]}
            />
            <h1>Loan Request Form</h1>
            {submitted ? (
                <div style={{ color: 'green', marginTop: 24 }}>
                    Thank you for your loan request! We will review your application and contact you soon.
                </div>
            ) : (
                <form onSubmit={handleSubmit} style={{ display: 'flex', flexDirection: 'column', gap: 16 }}>
                    <label>
                        Loan Amount
                        <input
                            type="number"
                            value={amount}
                            onChange={e => setAmount(e.target.value)}
                            required
                            min="1"
                            style={{ width: '100%', padding: 8, marginTop: 4 }}
                        />
                    </label>
                    <label>
                        Loan Purpose
                        <input
                            type="text"
                            value={purpose}
                            onChange={e => setPurpose(e.target.value)}
                            required
                            style={{ width: '100%', padding: 8, marginTop: 4 }}
                        />
                    </label>
                    <label>
                        Repayment Period (months)
                        <input
                            type="number"
                            value={period}
                            onChange={e => setPeriod(e.target.value)}
                            required
                            min="1"
                            style={{ width: '100%', padding: 8, marginTop: 4 }}
                        />
                    </label>
                    <label>
                        Special Request
                        <textarea
                            value={special}
                            onChange={e => setSpecial(e.target.value)}
                            rows={4}
                            placeholder="Enter any special request or information here..."
                            style={{ width: '100%', padding: 8, marginTop: 4 }}
                        />
                    </label>
                    <button type="submit" style={{ padding: '10px 0', background: '#22223b', color: '#fff', border: 'none', borderRadius: 4, fontWeight: 600 }}>
                        Submit Loan Request
                    </button>
                </form>
            )}
            <Footer />
        </main>
    );
};

export default Contact;
