import React, { useEffect, useState } from 'react';
import { Project } from '../types';
import Footer from '../components/Footer';

const ProjectUpload: React.FC<{ onUpload: (project: Project) => void }> = ({ onUpload }) => {
    const [title, setTitle] = useState('');
    const [description, setDescription] = useState('');
    const [link, setLink] = useState('');
    const [image, setImage] = useState('');

    const handleSubmit = (e: React.FormEvent) => {
        e.preventDefault();
        if (!title || !description || !link) return;
        onUpload({ id: Date.now(), title, description, link, image });
        setTitle('');
        setDescription('');
        setLink('');
        setImage('');
    };

    return (
        <form onSubmit={handleSubmit} style={{ marginBottom: 32, background: '#f7f9fa', padding: 16, borderRadius: 8 }}>
            <h2>Upload New Project</h2>
            <input
                type="text"
                placeholder="Project Title"
                value={title}
                onChange={e => setTitle(e.target.value)}
                style={{ display: 'block', marginBottom: 8, width: '100%', padding: 8 }}
                required
            />
            <textarea
                placeholder="Project Description"
                value={description}
                onChange={e => setDescription(e.target.value)}
                style={{ display: 'block', marginBottom: 8, width: '100%', padding: 8 }}
                required
            />
            <input
                type="url"
                placeholder="Project Link"
                value={link}
                onChange={e => setLink(e.target.value)}
                style={{ display: 'block', marginBottom: 8, width: '100%', padding: 8 }}
                required
            />
            <input
                type="url"
                placeholder="Image URL (optional)"
                value={image}
                onChange={e => setImage(e.target.value)}
                style={{ display: 'block', marginBottom: 8, width: '100%', padding: 8 }}
            />
            <button type="submit" style={{ padding: '8px 24px', background: '#22223b', color: '#fff', border: 'none', borderRadius: 4 }}>Upload</button>
        </form>
    );
};
const Projects: React.FC = () => {
    const [projects, setProjects] = useState<Project[]>([]);


    // Commented out API fetch for demo upload
    // useEffect(() => {
    //     const fetchProjects = async () => {
    //         const response = await fetch('/api/projects'); // Adjust the API endpoint as needed
    //         const data = await response.json();
    //         setProjects(data);
    //     };
    //     fetchProjects();
    // }, []);

    const handleUpload = (project: Project) => {
        setProjects(prev => [project, ...prev]);
    };

    return (
        <div className="projects">
            <h1>My Projects</h1>
            <ProjectUpload onUpload={handleUpload} />
            <ul>
                {projects.map((project) => (
                    <li key={project.id} style={{ marginBottom: 24, background: '#fff', borderRadius: 8, boxShadow: '0 2px 8px rgba(34,34,59,0.07)', padding: 16 }}>
                        {project.image && <img src={project.image} alt={project.title} style={{ width: 100, height: 100, objectFit: 'cover', borderRadius: 8, marginBottom: 8 }} />}
                        <h2>{project.title}</h2>
                        <p>{project.description}</p>
                        <a href={project.link} target="_blank" rel="noopener noreferrer">View Project</a>
                    </li>
                ))}
            </ul>
            <Footer />
        </div>
    );
};

export default Projects;
